mapper.select:
select date_format(max(create_time),'%Y-%m-%d %H:%i:%s') from tavern_audit_result
where month = #{query.month}
and op_time = (select max(op_time) from tavern_audit_result where month = #{query.month})

select FORMAT(sum(settlement_Amount),2) as settlement_Amount ,FORMAT(sum(net_Profits),2) as net_Profits ,FORMAT(sum(price_Clean),2) as price_Clean ,FORMAT(sum(sum_Expend),2) as sum_Expend ,FORMAT(sum(actual_Zj),2) as actual_Zj ,FORMAT(sum(kh_Fee),2) as kh_Fee ,FORMAT(sum(bj_Fee),2) as bj_Fee ,FORMAT(sum(bc_Expend),2) as bc_Expend ,FORMAT(sum(daily_Expend),2) as daily_Expend ,FORMAT(sum(commission ),2) as commission from tavern_audit_result where 1=1
and month = #{query.month} and op_time = (select max(op_time) from tavern_audit_result where month = #{query.month})
and tgfd = #{query.tgfd}

select format(sum(amount),2) as bkft_fee
from tavern_room_bkft where 1=1

    and month = #{query.month}


    and tgfd = #{query.tgfd}
